Toro.MensagemController=Ember.ArrayController.extend({sortProperties:["data"],sortAscending:!1,needs:["candidato"],actions:{comentario:function(o,e){var t=this.get("model");Ember.$.post(Toro.configs.webservice_uri+"/comentario",{usuario:1,candidato:e._id,comentario:o}).then(function(o){t.pushObject(o)}),this.set("comentario","")}}});